function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	 alert("You have changed the Requested for from " + oldValue + " to " + newValue + ".");

	var whatneeded = g_form.getValue('u_state');
         alert(whatneeded);
		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('u_state');

		// If the the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'india'){
			g_form.addOption('u_state','tg','Telangana');
			g_form.addOption('u_state','ap','Andhra pradesh');
			g_form.addOption('u_state','other','Other');
		}
		// If the the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
		if(newValue == 'us'){
			g_form.addOption('u_state','dallas','Dallas');
			g_form.addOption('u_state','texas','Texas');
			g_form.addOption('u_state','other','Other');
		}
		// If the the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		if(newValue == 'uk'){
			g_form.addOption('u_state','uk1','UK1');
			g_form.addOption('u_state','uk2','Uk2');
			g_form.addOption('u_state','other','Other');
		}
jslog("This message is from jslog()-"+g_form.getValue('u_state'));   
		// If the form is loading and it is not a new record, set the u_what_needed value to the
		// value from the record before it was loaded
// 		if(isLoading && !g_form.isNewRecord()){
//g_form.setValue('u_state', 'tg');
// 		}

   //Type appropriate comment here, and begin script below

}
